// Componente Modal para Duplicar OT
const DuplicateOTModal: React.FC<{
  isOpen: boolean;
  onClose: () => void;
  otNumber: string;
  incrementSuffix: (ot: string) => string;
  onDuplicate: (options: {
    copyClientEquipment: boolean;
    copyBudgets: boolean;
    copyObservations: boolean;
    copyReportTecnico: boolean;
    newOtSuffix: string;
  }) => void;
}> = ({ isOpen, onClose, otNumber, incrementSuffix, onDuplicate }) => {
  const [copyClientEquipment, setCopyClientEquipment] = React.useState(true);
  const [copyBudgets, setCopyBudgets] = React.useState(true);
  const [copyObservations, setCopyObservations] = React.useState(false);
  const [copyReportTecnico, setCopyReportTecnico] = React.useState(false);
  const [newOtSuffix, setNewOtSuffix] = React.useState('');

  React.useEffect(() => {
    if (isOpen) {
      setNewOtSuffix(incrementSuffix(otNumber));
    }
  }, [isOpen, otNumber, incrementSuffix]);

  const handleDuplicate = () => {
    onDuplicate({
      copyClientEquipment,
      copyBudgets,
      copyObservations,
      copyReportTecnico,
      newOtSuffix: newOtSuffix || incrementSuffix(otNumber)
    });
  };

  return (
    <div 
      className={`fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[100] flex items-center justify-center p-4 no-print transition-all duration-300 ${isOpen ? 'opacity-100 visible' : 'opacity-0 invisible pointer-events-none'}`}
      onClick={onClose}
    >
      <div className="bg-white rounded-3xl p-8 max-w-md w-full shadow-2xl" onClick={e => e.stopPropagation()}>
        <h3 className="text-lg font-black text-slate-900 uppercase tracking-tighter mb-4">Duplicar OT</h3>
        <p className="text-xs text-slate-600 mb-6">Seleccione qué datos copiar a la nueva OT:</p>
        
        <div className="space-y-4 mb-6">
          <label className="flex items-center gap-3 cursor-pointer">
            <input
              type="checkbox"
              checked={copyClientEquipment}
              onChange={(e) => setCopyClientEquipment(e.target.checked)}
              className="w-4 h-4 text-purple-600 rounded focus:ring-purple-500"
            />
            <span className="text-sm font-medium text-slate-700">Cliente y Equipo</span>
          </label>

          <label className="flex items-center gap-3 cursor-pointer">
            <input
              type="checkbox"
              checked={copyBudgets}
              onChange={(e) => setCopyBudgets(e.target.checked)}
              className="w-4 h-4 text-purple-600 rounded focus:ring-purple-500"
            />
            <span className="text-sm font-medium text-slate-700">Presupuestos</span>
          </label>

          <label className="flex items-center gap-3 cursor-pointer">
            <input
              type="checkbox"
              checked={copyObservations}
              onChange={(e) => setCopyObservations(e.target.checked)}
              className="w-4 h-4 text-purple-600 rounded focus:ring-purple-500"
            />
            <span className="text-sm font-medium text-slate-700">Observaciones / Acciones a tomar</span>
          </label>

          <label className="flex items-center gap-3 cursor-pointer">
            <input
              type="checkbox"
              checked={copyReportTecnico}
              onChange={(e) => setCopyReportTecnico(e.target.checked)}
              className="w-4 h-4 text-purple-600 rounded focus:ring-purple-500"
            />
            <span className="text-sm font-medium text-slate-700">Reporte técnico</span>
          </label>
        </div>

        <div className="mb-6">
          <label className="block text-sm font-medium text-slate-700 mb-2">
            Nueva OT:
          </label>
          <input
            type="text"
            value={newOtSuffix}
            onChange={(e) => setNewOtSuffix(e.target.value)}
            className="w-full border border-slate-300 rounded-lg px-3 py-2 text-sm font-mono"
            placeholder={incrementSuffix(otNumber)}
          />
        </div>

        <div className="flex gap-3">
          <button
            onClick={onClose}
            className="flex-1 bg-slate-200 text-slate-700 font-black px-6 py-3 rounded-xl uppercase tracking-widest text-xs transition-all hover:bg-slate-300"
          >
            Cancelar
          </button>
          <button
            onClick={handleDuplicate}
            className="flex-1 bg-purple-600 text-white font-black px-6 py-3 rounded-xl uppercase tracking-widest text-xs transition-all hover:bg-purple-700 shadow-lg"
          >
            Crear OT
          </button>
        </div>
      </div>
    </div>
  );
};

const MobileSignatureView: React.FC<{ 
  ot: string; 
  razonSocial: string; 
  firebase: FirebaseService;
  shareReportPDF: (ot?: string) => Promise<void>;
  isSharing: boolean;
}> = ({ ot, razonSocial, firebase, shareReportPDF, isSharing }) => {
  const [signed, setSigned] = useState(false);
  const padRef = useRef<SignaturePadHandle>(null);

  const handleConfirmFirma = async () => {
    const dataUrl = padRef.current?.getSignature();
    if (!dataUrl) return alert("Por favor, realice la firma antes de confirmar.");
    
    try {
      // Guardar firma en Firebase con await
      await firebase.updateSignature(ot, dataUrl);
      setSigned(true);
      
      // Mostrar mensaje de éxito por 700-900ms antes de redirigir
      setTimeout(() => {
        const url = new URL(window.location.href);
        url.searchParams.delete('modo');
        window.location.href = url.toString();
      }, 800);
    } catch (error) {
      console.error("Error al guardar firma:", error);
      alert("Error al guardar la firma. Por favor, intente nuevamente.");
    }
  };

  return (
    <div className="min-h-screen bg-slate-50 flex flex-col items-center justify-center p-6 text-center">
      <div className="w-full max-w-md bg-white rounded-3xl p-8 shadow-2xl border border-slate-100">
        <div className="mx-auto mb-6 flex justify-center">
          <CompanyLogo />
        </div>
        <h1 className="text-lg font-black text-slate-800 uppercase mb-1">Firma de Conformidad</h1>
        <p className="text-[10px] bg-blue-50 text-blue-600 font-black px-4 py-1.5 rounded-full inline-block uppercase tracking-widest mb-2">Reporte {ot}</p>
        <p className="text-[11px] font-bold text-slate-500 uppercase mb-6">{razonSocial || "Servicio Técnico"}</p>
        
        {!signed ? (
          <div className="space-y-6">
            <p className="text-xs text-slate-500 italic">Por favor, firme en el recuadro para validar el servicio.</p>
            <SignaturePad ref={padRef} label="" onClear={() => {}} />
            <button 
              onClick={handleConfirmFirma}
              className="w-full bg-slate-900 text-white font-black py-4 rounded-2xl uppercase text-[11px] tracking-widest shadow-xl active:scale-95 transition-all"
            >
              Confirmar Firma
            </button>
          </div>
        ) : (
          <div className="py-10 animate-in zoom-in duration-500">
            <div className="w-20 h-20 bg-emerald-100 text-emerald-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-10 h-10" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" /></svg>
            </div>
            <h2 className="text-lg font-black text-slate-900 uppercase">¡Firma Registrada!</h2>
            <p className="text-xs text-slate-400 mt-2">Su firma ha sido sincronizada exitosamente.</p>
            <button
              onClick={() => shareReportPDF(ot)}
              disabled={isSharing}
              className="mt-6 w-full bg-purple-600 text-white font-black py-3 rounded-2xl uppercase text-[11px] tracking-widest shadow-xl active:scale-95 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isSharing ? 'Preparando...' : 'Compartir / Enviar PDF'}
            </button>
            <p className="text-[10px] text-slate-300 mt-4 uppercase font-bold">Puede compartir el reporte ahora o continuar al reporte completo</p>
          </div>
        )}
      </div>
    </div>
  );
};

